import React, { useState, useContext } from 'react';
import LocalContext from '../Local/context';
import { Container, Button } from './styles';

function Login() {
    const [state, setState] = useState({accessKind: "local",
                                        storeNumber: 1,
                                        accessCode: ""});

    function handleLogin() {

    }

    return (
        <Container>
            <h1>Fluxo de clientes</h1>
            <h2>Lojas Brasil</h2>
            <h3>Login</h3>
            <h4>Tipo de acesso</h4>
            <select className="accessKind" onChange={e => setState({...state, accessKind: e.target.value})}>
                <option value="local">Loja</option>
                <option value="global">Empresa</option>
            </select>
            {state.accessKind === "local" ?
            <>
            <h4>Número da loja</h4>
            <input className="inputStore" type="number" onChange={e => setState({...state, storeNumber: e.target.value})}/>
            </> :
            <></>}
            <h4>Código de acesso</h4>
            <input className="inputCode" type="password" onChange={e => setState({...state, accessCode: e.target.value})} />


            <Button className="enter" onClick={handleLogin}>Entrar</Button>
        </Container>
    );
}

export default Login;
