import styled from 'styled-components';

const standardGray = "#dcdcdc";
const standardDarkBlue = "#000066";
const standardLightBlue = "#009999";

export const Container = styled.div`
    @import url('https://fonts.googleapis.com/css2?family=Montserrat&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Lobster&display=swap');

    width: 100%;
    height: 100%;
    max-width: 1280px;
    margin: 0 auto;
    padding: 15vw;
    background-color: ${standardGray};
    font-family: Montserrat;

    display: flex;
    flex-direction: column;
    justify-content: center
    align-items: center;

    h1 {
        color: ${standardDarkBlue};
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        font-size: 2em;
        font-weight: 900;
        text-align: center;
    }

    h2 {
        color: ${standardDarkBlue};
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        font-size: 1.5em;
        font-weight: 800;
        text-align: center;
    }

    h3 {
        color: ${standardDarkBlue};
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        font-size: 1.2em;
        font-weight: 500;
        text-align: center;
    }

    h4 {
        color: ${standardLightBlue};
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        font-size: 1em;
        font-weight: 400;
        text-align: center;
    }

    select {
        font-family: Montserrat;
        background-color: white;
        color: #323232;
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        padding: 0.5em;
        font-size: 1em;
    }

    input {
        color: #323232;
        margin-block-start: 0.5em;
        margin-block-end: 0.5em;
        padding: 0.5em;
        font-size: 1em;
    }
`;

export const Button = styled.button`
  cursor: ${props => props.checkDisabled ? "default" : "pointer"};
  border-color: white;
  border-radius: 0.5em;
  color: #555555;
  margin-block-start: 0.5em;
  margin-block-end: 0.5em;
  padding: 0em;
  font-size: 1em;
  width: ${props => props.fullWidth ? "100%" : "5em"};
  height: 2em;

  &:hover {
    background: white;
  }

  opacity: ${props => props.checkDisabled ? 0.6 : 1};
`;
