import React, { useState } from 'react';
import { Text } from '../../pages/Local/styles';
import { XYPlot, LineSeries, VerticalGridLines, HorizontalGridLines, XAxis, YAxis } from 'react-vis';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, KeyboardDateTimePicker } from '@material-ui/pickers';

function Statistics() {
    const [state, setState] = useState({minDate: new Date((new Date(Date.now())).getFullYear(), (new Date(Date.now())).getMonth(), (new Date(Date.now())).getDate(), 0, 0, 0, 0),
                                        maxDate: new Date((new Date(Date.now())).getFullYear(), (new Date(Date.now())).getMonth(), (new Date(Date.now())).getDate(), 23, 59, 59, 999),
                                        timeScale: "hours"});

    const data = [
        {x: 0, y: 8},
        {x: 1, y: 5},
        {x: 2, y: 4},
        {x: 3, y: 9},
        {x: 4, y: 1},
        {x: 5, y: 7},
        {x: 6, y: 6},
        {x: 7, y: 3},
        {x: 8, y: 2},
        {x: 9, y: 5}
    ];

    const handleMinDate = (date: Date | null) => {
        const timeDif = state.maxDate - date;
        if (timeDif > 0) {
            if (timeDif <= 7200000) setState({...state, minDate: date, timeScale: "minutes"});
            else if (timeDif <= 172800000) setState({...state, minDate: date, timeScale: "hours"});
            else if (timeDif <= 5259600000) setState({...state, minDate: date, timeScale: "days"});
            else setState({...state, minDate: date, timeScale: "months"});
        }
    }

    const handleMaxDate = (date: Date | null) => {
        const timeDif = date - state.minDate;
        if (timeDif > 0) {
            if (timeDif <= 7200000) setState({...state, maxDate: date, timeScale: "minutes"});
            else if (timeDif <= 172800000) setState({...state, maxDate: date, timeScale: "hours"});
            else if (timeDif <= 5259600000) setState({...state, maxDate: date, timeScale: "days"});
            else setState({...state, maxDate: date, timeScale: "months"});
        }
    }

    return (
        <>
        <Text><span>Selecionar período de visualização:</span></Text>
        <MuiPickersUtilsProvider utils={DateFnsUtils}>
            <KeyboardDateTimePicker 
                variant="inline" ampm={false}
                label="Limite inferior"
                value={state.minDate} format="dd/MM/yyyy HH:mm"
                onChange={handleMinDate} disableFuture
            />
            <KeyboardDateTimePicker 
                variant="inline" ampm={false}
                label="Limite superior"
                value={state.maxDate} format="dd/MM/yyyy HH:mm"
                onChange={handleMaxDate} disableFuture minDate={state.minDate}
            />
        </MuiPickersUtilsProvider>

        <Text><span>Gráficos</span></Text>


        <Text><span>Tabelas</span></Text>


        <Text><span>Indicadores</span></Text>

        <XYPlot width={300} height={300}>
            <LineSeries data={data} />
            <VerticalGridLines />
            <HorizontalGridLines />
            <XAxis />
            <YAxis />
        </XYPlot>
        {console.log(state)}
        </>
    );
}

export default Statistics;
